awk '/^[[:space:]]*MCC [0-9][0-9][0-9][0-9]/,/^[[:space:]]*MCC Category/ {if ($0 ~ /^[[:space:]]*MCC [0-9][0-9][0-9][0-9]/) {code = gensub(/ /, "", "g", substr($0, 5, 4)); if (length(code) > 0 )printf("\"%s\",", code)}; if ($0 ~ /^[[:space:]]*MCC Category/) {match($0, /Category[[:space:]]+/); printf("\"%s\"\n", substr($0, RSTART + RLENGTH))}}' datafiles/quick-reference-booklet-merchant-edition.txt >mastercard_supplementry.txt
